export declare const string = "<ng-container *ngFor=\"let prompt of AckApp.prompts.prompts;let i=index\"><ack-modal-layout [allowClose]=\"0\"><div style=\"text-align:left;max-width:900px;border:1px solid #DDD;border-radius:5px;margin:1em;padding:1em;background-color:white;\"><h3 *ngIf=\"prompt.title\" style=\"margin-top:0;\">{{ prompt.title }}</h3><div style=\"padding-right:1em;\">{{ prompt.message }}</div><br/><br/><div style=\"text-align:right;\"><a *ngIf=\"prompt.type=='confirm'\" (click)=\"prompt.emitter.emit(false)\" style=\"text-align:center;display:inline-block;width:75px;border-radius:3px;border:1px solid #AAA;padding:0 1em;margin-right:.5em;\">cancel</a><a (click)=\"prompt.emitter.emit(true)\" style=\"text-align:center;display:inline-block;width:75px;border-radius:3px;border:1px solid #AAA;padding:0 1em;\" [ngStyle]=\"{'font-weight':prompt.type=='confirm' ? 'bold' : null}\">ok</a></div></div></ack-modal-layout></ng-container><ng-container *ngFor=\"let modal of AckApp.modals;let i=index\"><ng-template [ngTemplateOutlet]=\"modal.layout\"></ng-template></ng-container>";
