export declare const string = "<div class=\"pad-xxs\" *ngIf=\"error!=null &amp;&amp; errorClose!=error\" [@200]=\"'fadeInUp'\" [ngClass]=\"cssClasses\"><div class=\"flex-valign-center flex-wrap child-margin-xxs\"><div class=\"flex-1\"><div class=\"strong text-3x margin-0 cursor-pointer\" (click)=\"moreDetails=!moreDetails\">{{ getErrorMessage(error) }}</div></div><a class=\"text-blue underline text-xs\" *ngIf=\"error &amp;&amp; (error|typeof)!='string'\" (click)=\"moreDetails=!moreDetails\">details</a><a class=\"text-blue underline text-xs\" *ngIf=\"error\" (click)=\"errorClose=error\">close</a></div><div *ngIf=\"moreDetails\" [@200]=\"'fadeInUp'\"><absolute-overflow-x class=\"text-sm\"><pre class=\"margin-0 pad-xs\">{{ error|json }}</pre></absolute-overflow-x></div></div>";
